import { useEffect, useState } from "react";
// import img1 from "../../assets/images/Abril_D.png";
// // import img2 from "../../assets/images/Agosto_D.png";
// import img3 from "../../assets/images/Otra_Imagen.png";
 

const Card = () => {
    const [burgers, setBurgers] = useState([]);
    
    const [patito, setPatito] = useState("nada");

    useEffect(() => {
        fetch("https://apimocha.com/burgergrill/posts")
        .then((response) => response.json())
        .then((data) => setBurgers(data))
        .catch((error) => console.error("Error al cargar los datos", error));
    }, []);
    
// const images = [img1, img2];
setPatito("Hola mundo")
  return (
    <>
        <div>{patito}</div>
        <div className="min-h-screen bg-gray-100 p-6">
            <h1 className="text-3xl font-bold text-center mb-6">Men√∫ de Hamburguesas</h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {burgers.map((burger, index) => (
        <div key={index} className="bg-white p-4 shadow-lg rounded-lg">
            <img src={burger.img} alt={burger.nombre} className="w-full h-48 object-cover rounded-md" />
            <h2 className="text-xl font-bold mt-4">{burger.nombre}</h2>
            <p className="text-gray-600 mt-2">{burger.descripcion}</p>
            <p className="text-lg font-semibold text-green-600 mt-2">${burger.valor.toFixed(2)}</p>
</div>
        ))}
</div>
</div>
</>
  );
};
 
export default Card;
