import { useEffect, useState } from "react";
import './card.css'
// import imgen from "../../assets/images/Agosto_D.png"

const Card = () => {
  const [burgers, setBurgers] = useState([]);
    useEffect(() => {
      fetch("https://apimocha.com/burgergrill/posts")
      .then((response) => response.json())
      .then((data) => setBurgers(data))
      .catch((error) => console.error("Error al cargar los datos", error));
    });

  // const imagen = [imgen]
  return (
    <div className="slider">
      <div className="slider">
        <h1 className="title-card">Starters</h1>
        <div className="content-card">
          {burgers.map((burger, index) => (
            <div key={index} className="decription-card">
              <img src={burger.img} alt={burger.nombre} className="img-card" />
              <h2 className="name-product">{burger.nombre}</h2>
              <p className="description-product">{burger.descripcion}</p>
              <p className="valor-product">${burger.valor.toFixed(2)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
 
export default Card;
